fragment:
#version 300 es

in lowp vec3 fragColor;
in lowp vec3 fragEdgeDistance;
out lowp vec3 color;

void main(){
    color = fragColor;
    if(fragEdgeDistance.x > 0.9 || fragEdgeDistance.y > 0.9 || fragEdgeDistance.z > 0.9){
        color = vec3(1, 0, 0);
    }
}

vertex:
#version 300 es

in vec2 worldVertexPosition;
in vec3 edgeDistance;
in vec3 vertexColor;
uniform mat3 VIEW;

out vec3 fragColor;
out vec3 fragEdgeDistance;

void main(){
    vec3 transforedPosition = VIEW * vec3(worldVertexPosition.x, worldVertexPosition.y, 1);
    gl_Position = vec4(transforedPosition, 1);
    fragColor = vertexColor;
    fragEdgeDistance = edgeDistance;
}
